
public class F_ {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		//if~else
		 int score =67; //score가 60이상이면 합격 그렇지 않으면 불합격으로 출력
		 if(score < 60) {
			 System.out.println("불합격"); 
		 }else {
			 System.out.println("합격");
	     }
		
		 if(!(score < 60)) {   //!는 반대로
			 System.out.println("합격"); 
		 }else {
			 System.out.println("불합격");
	     }
		 //else는 단독으로 쓰이지만 else if()는 괄호안에 무조건 수가 있어야함
		 
	
	}

}
